import React from 'react'

import {FormGroup,FormControl,InputLabel, Input,styled,Button,Typography} from '@mui/material/';
import { useState } from 'react';

const Container= styled(FormGroup)`
width:50%;
margin:5% auto 0 auto;
> div{
     margin-top:15px;
    color:#a86b4c;
}
`

 const defaultvalue={
    name:"",
    username:"",
    email:"",
    phone:""
} 

function AddUsers() {

     const[user,setuser]=useState(defaultvalue);


     const onchangevalue=(e)=>{

        console.log(e.target.name,e.target.values);
        setuser({...user,[e.target.name]:e.target.values})
        console.log(user);

        }

  return (
    <>
       <Container>
       <Typography variant="h3" gutterBottom>ADDUSER</Typography>
        <FormControl>
            <InputLabel>Name</InputLabel>
            <Input onChange={(e)=>onchangevalue(e)} name='name'></Input>
        </FormControl>
        <FormControl>
            <InputLabel>UserName</InputLabel>
            <Input onChange={(e)=>onchangevalue(e)} name='username'></Input>
        </FormControl>
        <FormControl>
            <InputLabel>Email</InputLabel>
            <Input onChange={(e)=>onchangevalue(e)} name='email'></Input>
        </FormControl>
        <FormControl>
            <InputLabel>Phone</InputLabel>
            <Input onChange={(e)=>onchangevalue(e)} name='phone '></Input>
        </FormControl>
        <FormControl>
        <Button variant="contained">AddUser</Button> 
        </FormControl>
       </Container>
    </>
  )
}

export default AddUsers;
