import { Table, TableHead, TableRow, TableCell, styled, TableBody,Button} from '@mui/material/';
import { getUsers } from '../Services/api';

const Container = styled(Table)`
width:90%;
margin:19px auto 0 auto;
`


function AllUser() {

const[users,setusers]=useState([])



  useEffect(() => {

    getAllUsers();
  }, [])



